import React, {useContext, useEffect} from 'react';
import {Container, Image, ListGroup} from "react-bootstrap";
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {fetchProducts, fetchTypes} from "../http/productAPI";
import {Swiper} from "swiper";
import ProductList from "../components/ProductList";
import data from "bootstrap/js/src/dom/data";
import ProductItem from "../components/ProductItem";
import {SwiperSlide} from "swiper/react/swiper-react";

const Shop = observer(() => {

    const {product} = useContext(Context)
    let startProd = ''

    useEffect(() => {
        fetchProducts().then(data => product.setProducts(data))
    }, [])

    const start = () => {
        product.products.map(product => {
                console.log(product.id)
                if (product.id === 8) {
                    return startProd = product
                } else {
                    console.log('sdds')
                }
            }
        )
    }

    start()

    return (
        <Container style={{
            margin: 0,
            padding: 0,
            marginTop: 60,
            display: "flex",
            alignItems: "center",
            flexDirection: "column"}}>
            <ProductItem key={startProd.id} product={startProd}/>
            <h1 style={{
                fontSize: 28,
                color: 'white',
                lineHeight: '36px',
                alignSelf: 'start',
                marginLeft: 32,
                fontWeight: 'normal',
                marginTop: -150,
                fontFamily: 'Raleway'
            }}>Собери худи<br />
                <span style={{
                    fontSize: 42,
                    lineHeight: '50px',
                    fontWeight: 'bold'
                }}>на свой вкус</span>
            </h1>
            <div style={{
                display: 'flex',
                flexDirection: 'row',
                width: '80%',
                justifyContent: 'space-between',
                alignItems: 'start'
            }}>
                <p style={{
                    fontSize: 28,
                    color: 'white',
                    lineHeight: '36px',
                    fontFamily: 'Raleway'
                }}>01</p>
                <div>
                    <p style={{
                        fontSize: 28,
                        color: 'white',
                        lineHeight: '36px',
                        fontFamily: 'Raleway'
                    }}>Выбери цвет базы</p>
                    <p style={{

                    }}>Это цвет самой основы без кармана и капюшона</p>
                </div>
            </div>
        </Container>
    );
});

export default Shop;
