import React from "react";
import Popup from "./Popup";
import hoodyBlack from '../images/hoody_black.png';
import hoodyWhite from '../images/hoody_white.png';
import hoodyGrey from '../images/hoody_grey.png';

let selectedHoody = {};

function Step1() {

    const radios = document.querySelectorAll('.step1__radio');
    // const labels = document.querySelectorAll('.step1__variant');
    const container = document.querySelector('.step1__choose');
    const input3 = container.children[3];
    const input6 = container.children[6];
    const input0 = container.children[0];
    const label1 = container.children[1];
    const label4 = container.children[4];
    const label7 = container.children[7];


    function handleLeft(){
        if (input0 && input3 && input6 && label1 && label4 && label7) {
            container.children[3].attributes.defaultChecked = false
            container.children[6].attributes.defaultChecked = true
            let saveInner1 = container.children[0].innerHTML
            let saveInner2 = container.children[1].innerHTML
            container.children[0].innerHTML = container.children[3].innerHTML
            container.children[1].innerHTML = container.children[4].innerHTML
            container.children[3].innerHTML = container.children[6].innerHTML
            container.children[4].innerHTML = container.children[7].innerHTML
            container.children[6].innerHTML = saveInner1
            container.children[7].innerHTML = saveInner2;
            selectedBase();
        } else {
            console.log('Not found')
        }
    }


    function handleRight(){
        container.children[3].attributes.defaultChecked = false
        container.children[0].attributes.defaultChecked = true
        let saveInner1 = container.children[6].innerHTML
        let saveInner2 = container.children[7].innerHTML
        container.children[6].innerHTML = container.children[3].innerHTML
        container.children[7].innerHTML = container.children[4].innerHTML
        container.children[3].innerHTML = container.children[0].innerHTML
        container.children[4].innerHTML = container.children[1].innerHTML
        container.children[0].innerHTML = saveInner1
        container.children[1].innerHTML = saveInner2;
        selectedBase();
    }

    function selectedBase(){
        for (let i = 0; i < radios.length; i++) {
            // eslint-disable-next-line no-cond-assign
            if (radios[i].checked === true) {
                selectedHoody = radios[i].labels[0].children[0].attributes.src.nodeValue;
                console.log(selectedHoody)
            return
            }
        }
    }

    function hidePopup(){
        document.querySelector('#popup').classList.remove('popup_opened')
    }

    document.addEventListener('DOMContentLoaded', selectedBase);
    setTimeout(hidePopup, 3000);

    return(
        <div className='step1'>
            <Popup 
            number='01'
            text='Для начала выберем цвет основы'
            />
            <div id='step1Choose' className='step1__choose'>
                <input name='step1' type='radio' id='radio-1' value='hoodyWhite' className='step1__radio' disabled='true' />
                <label className='step1__variant' htmlFor='radio-1'>
                    <img className='step1__image' src={hoodyWhite} alt={hoodyWhite} />
                </label>
                <button className='step1__left-arrow' onClick={handleLeft} />
                <input name='step1' type='radio' id='radio-2' value='hoodyBlack' className='step1__radio step1__radio_active' defaultChecked />
                <label className='step1__variant' htmlFor='radio-2'>
                    <img className='step1__image' src={hoodyBlack} alt={hoodyBlack} />
                </label>
                <button className='step1__right-arrow' onClick={handleRight} />
                <input name='step1' type='radio' id='radio-3' value='hoodyGrey' className='step1__radio' disabled='true' />
                <label className='step1__variant' htmlFor='radio-3'>
                    <img className='step1__image' src={hoodyGrey} alt={hoodyGrey} />
                </label>
            </div>   
        </div>
    )
}

export {selectedHoody, Step1};
