import React, {useContext} from 'react';
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {Row} from "react-bootstrap";
import ProductItem from "./ProductItem";
import {Swiper, SwiperSlide} from "swiper/react/swiper-react";
import 'swiper/swiper-bundle.css'
import {EffectCards, EffectCoverflow, EffectCreative, EffectCube, EffectFade, EffectFlip} from "swiper";

const ProductList = observer(() => {
    const {product} = useContext(Context)
    return (
        <Swiper
            style={{width: 320, display: "flex", alignItems: "center"}}
            spaceBetween={50}
            slidesPerView={3}
            modules={[EffectFade]}
            effect="fade"
            loop={true}
            onSlideChange={() => console.log('slide change')}
            onSwiper={(swiper) => console.log(swiper)}
        >
            {product.products.map(product =>
                <SwiperSlide style={{display: "flex", justifyContent: "center"}}><ProductItem key={product.id} product={product}/></SwiperSlide>
            )}
        </Swiper>
    );
});

export default ProductList;
