import React from "react";
import Popup from "./Popup";
// import hoodyBlack from '../images/hoody_black.png';
// import hoodyWhite from '../images/hoody_white.png';
// import hoodyGrey from '../images/hoody_grey.png';
import { selectedHoody } from "./Step1";
import polovina from '../images/polovina.png';
import full from '../images/full.png';
import vn from '../images/vn.png';
import fullPocket from '../images/fullPocket.png';

let selectedCaption = ''

export default function Step2() {

    const radios = document.querySelectorAll('.step1__radio');
    // const labels = document.querySelectorAll('.step1__variant');
    const container = document.querySelector('.step1__choose');
    let centerCaption = document.querySelector('#centerCaption')

    function handleCaptionLeft(){
        container.children[3].attributes.defaultChecked = false
        container.children[6].attributes.defaultChecked = true
        let saveInner1 = container.children[0].innerHTML
        let saveInner2 = container.children[1].innerHTML
        container.children[0].innerHTML = container.children[3].innerHTML
        container.children[1].innerHTML = container.children[4].innerHTML
        container.children[3].innerHTML = container.children[6].innerHTML
        container.children[4].innerHTML = container.children[7].innerHTML
        container.children[6].innerHTML = saveInner1;
        container.children[7].innerHTML = saveInner2;
        console.log([centerCaption])
        // selectedCaptions();
        // console.log([container.children[1].children[1]])
    }

    function handleCaptionRight(){
        container.children[3].attributes.defaultChecked = false
        container.children[0].attributes.defaultChecked = true
        let saveInner1 = container.children[6].innerHTML
        let saveInner2 = container.children[7].innerHTML
        container.children[6].innerHTML = container.children[3].innerHTML
        container.children[7].innerHTML = container.children[4].innerHTML
        container.children[3].innerHTML = container.children[0].innerHTML
        container.children[4].innerHTML = container.children[1].innerHTML
        container.children[0].innerHTML = saveInner1
        container.children[1].innerHTML = saveInner2;
        // selectedCaptions();
        // console.log([container.children[1].children[1]])    
    }

    function hidePopup(){
        document.querySelector('#popup').classList.remove('popup_opened')
    }

    function selectedCaptions(){
        for (let i = 0; i < radios.length; i++) {
            // eslint-disable-next-line no-cond-assign
            if (radios[i].checked === true) {
                selectedCaption = radios[i].labels[0].children[1].attributes.src.nodeValue;
                console.log(selectedCaption)
            return
            }
        }
    }

    // selectedCaptions();
    setTimeout(hidePopup, 3000)

    return(
        <div className='step1'>
            <Popup 
            number='02'
            text='Теперь дизайн капюшона и карманов'
            />
            <div className='step1__choose'>
                <input name='step2' type='radio' id='radio-11' value='' className='step1__radio' />
                <label className='step1__variant' for='radio-11'>
                    <img className='step1__image' src={selectedHoody} alt='' />
                    <img className='step2__caption' src={polovina} alt='' />
                    <img className='step2__pocket' src={fullPocket} alt='' />
                </label>
                <div className='step2__arrows'>
                    <button onClick={handleCaptionLeft} className='step1__left-arrow' />
                    <button className='step1__left-arrow' />
                </div>
                <input name='step2' type='radio' id='radio-12' value='' className='step1__radio' defaultChecked />
                <label className='step1__variant' for='radio-12'>
                    <img className='step1__image' src={selectedHoody} alt='' />
                    <img id='centerCaption' className='step2__caption' src={full} alt='' />
                    <img className='step2__pocket' src={fullPocket} alt='' />
                </label>
                <div className='step2__arrows'>
                    <button onClick={handleCaptionRight} className='step1__right-arrow' />
                    <button className='step1__right-arrow' />
                </div>
                <input name='step2' type='radio' id='radio-13' value='' className='step1__radio' />
                <label className='step1__variant' for='radio-13'>
                    <img className='step1__image' src={selectedHoody} alt='' />
                    <img className='step2__caption' src={vn} alt='' />
                    <img className='step2__pocket' src={fullPocket} alt='' />
                </label>
            </div>   
        </div>
    )
}
