import React, {useContext, useState} from 'react';
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {Row} from "react-bootstrap";
import ProductItem from "./ProductItem";
import {Swiper, SwiperSlide} from "swiper/react/swiper-react";
import 'swiper/swiper-bundle.css'
import {EffectCards, EffectCoverflow, EffectCreative, EffectCube, EffectFade, EffectFlip} from "swiper";
import GlitchSquiggly from 'react-glitch-effect/core/GlitchSquiggly'

const ProductList = observer(() => {
    const {product} = useContext(Context)
    const [isDisabled, setDisabled] = useState(false);

    const handleToggleGlitch = () => {
        setDisabled(!isDisabled);
    };
    return (
        <Swiper
            style={{width: 320, height: window.innerHeight, display: "flex", alignItems: "center", justifyContent: "center"}}
            spaceBetween={50}
            slidesPerView={1}
            modules={[EffectFade]}
            effect='fade'
            loop={true}
            onSlideChange={() => console.log('slide change')}
            onSwiper={(swiper) => handleToggleGlitch()}
        >
            {product.products.map(product =>
                <GlitchSquiggly
                    disabled={isDisabled}
                    baseFrequency={0.2}
                    scaleNoise={5}
                    duration={430}
                >
                    <SwiperSlide style={{display: "flex", justifyContent: "center"}}>
                        <ProductItem key={product.id} product={product}/>
                    </SwiperSlide>
                </GlitchSquiggly>
            )}
        </Swiper>
    );
});

export default ProductList;
