import {makeAutoObservable} from "mobx";

export default class ProductStore {
    constructor() {
        this._types = []
        this._colors = [
            {id: 1, name: 'base'}
        ]
        this._products = [
            {id: 1, name: 'black_base', price: 5000, img: '1.webp'},
            {id: 2, name: 'white_base', price: 5000, img: '2.webp'},
            {id: 3, name: 'grey_base', price: 5000, img: '3.webp'}
        ]
        makeAutoObservable(this)
    }

    setTypes(types) {
        this._types = types
    }
    setColors(colors) {
        this._colors = colors
    }
    setProducts(products) {
        this._products = products
    }

    get types() {
        return this._types
    }
    get colors() {
        return this._colors
    }
    get products() {
        return this._products
    }
}
