export default function Color(variant) {

    function choose() {
        const white = document.querySelector('.color_white')
        const black = document.querySelector('.color_black')
        const grey = document.querySelector('.color_grey')

        if (variant === 'color_white') {
            white.classList.add('color_big')
            black.classList.remove('color_big')
            grey.classList.remove('color_big')
        } else if (variant === 'color_black') {
            black.classList.add('color_big')
            white.classList.remove('color_big')
            grey.classList.remove('color_big')
        } else if (variant === 'color_grey') {
            grey.classList.add('color_big')
            black.classList.remove('color_big')
            white.classList.remove('color_big')
        } else {
            console.log('NoColor')
        }
    }

    return (
        <li className={`color ${variant}`} />
    )
}
