export default function Color(variant) {

    function check() {
        let colors = document.querySelectorAll(`.${variant}`)
        console.log(colors)
    }

    function clickColor() {
        let colors = document.querySelectorAll(`.${variant}`)
        for (let i; i < colors.length; i++) {
            if (colors[i].classList.contains('color_big')) {
                colors[i].classList.remove('color_big')
            }
        }
    }

    setTimeout(check, 2900)

    return (
        <li onClick={clickColor} className={`color ${variant}`} />
    )
}
