import React from "react";
import Popup from "./Popup";
import hoodyBlack from '../images/hoody_black.png';
import hoodyWhite from '../images/hoody_white.png';
import hoodyGrey from '../images/hoody_grey.png';


export default function Step1() {

    const radios = document.querySelectorAll('.step1__radio');
    // const labels = document.querySelectorAll('.step1__variant');
    const leftButton = document.querySelector('step1__left-arrow');
    // const rightButton = document.querySelector('step1__right-arrow');

    console.log(radios[0].labels[0])

    function handleLeft(){
        for(let i = 0; i < radios.length; i++){
            if (radios[i].checked = true) {
                radios[i].checked = false;
                radios[i - 1].checked = true;
            }
        }
    }

    function hidePopup(){
        document.querySelector('#popup').classList.remove('popup_opened')
    }

    leftButton.addEventListener('click', handleLeft);

    setTimeout(hidePopup, 3000)

    return(
        <div className='step1'>
            <Popup 
            number='01'
            text='Для начала выберем цвет основы'
            />
            <button className='step1__left-arrow' />
            <div className='step1__choose'>
                <input name='step1' type='radio' id='radio-1' value='white' className='step1__radio' />
                <label className='step1__variant' htmlFor='radio-1'>
                    <img className='step1__image' src={hoodyWhite} alt={hoodyWhite} />
                </label>
                <input name='step1' type='radio' id='radio-2' value='black' className='step1__radio' defaultChecked />
                <label className='step1__variant' htmlFor='radio-2'>
                    <img className='step1__image' src={hoodyBlack} alt={hoodyBlack} />
                </label>
                <input name='step1' type='radio' id='radio-3' value='grey' className='step1__radio' />
                <label className='step1__variant' htmlFor='radio-3'>
                    <img className='step1__image' src={hoodyGrey} alt={hoodyGrey} />
                </label>
            </div>   
            <button className='step1__right-arrow' />
        </div>
    )
}
