import { Route, Switch } from 'react-router';
import '../App.css';
import Header from './Header';
import Start from './Start';
import Step1 from './Step1';
import Popup from './Popup';
import BottomPanel from './BottomPanel';

function App() {
  return (
    <div className="App">
      <Header />
      <Switch>
        <Route exact path='/'>
          <Start />
        </Route>
        <Route path='/step1'>
          <Popup 
            number='01'
            text='Для начала выберем цвет основы'
          />
          <Step1 />
          <BottomPanel 
          back='/'
          next='/step2'
          />
        </Route>
        <Route path='/step2'>
          <Popup 
            number='02'
            text='Теперь дизайн капюшона и карманов'
          />
          <Step1 />
          <BottomPanel 
          back='/step1'
          next='/'
          />
        </Route>
      </Switch>
    </div>
  );
}

export default App;
