import React from "react";
import Popup from "./Popup";
// import hoodyBlack from '../images/hoody_black.png';
// import hoodyWhite from '../images/hoody_white.png';
// import hoodyGrey from '../images/hoody_grey.png';
import { selectedHoody } from "./Step1";
import polovina from '../images/polovina.png';
import polovina1 from '../images/polovina1.png';
import full from '../images/full.png';
import vn from '../images/vn.png';
import vn1 from '../images/vn1.png';

export default function Step2() {

    function hidePopup(){
        document.querySelector('#popup').classList.remove('popup_opened')
    }

    setTimeout(hidePopup, 3000)

    return(
        <div className='step1'>
            <Popup 
            number='02'
            text='Теперь дизайн капюшона и карманов'
            />
            <button className='step1__left-arrow' />
            <div className='step1__choose'>
                <input name='step2' type='radio' id='radio-11' value='' className='step1__radio' />
                <label className='step1__variant' for='radio-11'>
                    <img className='step1__image' src={selectedHoody} alt='' />
                    <img className='step2__caption' src={polovina} alt='' />
                    <img className='step2__caption' src={polovina1} alt='' />
                </label>
                <input name='step2' type='radio' id='radio-12' value='' className='step1__radio' defaultChecked />
                <label className='step1__variant' for='radio-12'>
                    <img className='step1__image' src={selectedHoody} alt='' />
                    <img className='step2__caption' src={full} alt='' />
                </label>
                <input name='step2' type='radio' id='radio-13' value='' className='step1__radio' />
                <label className='step1__variant' for='radio-13'>
                    <img className='step1__image' src={selectedHoody} alt='' />
                    <img className='step2__caption' src={vn} alt='' />
                    <img className='step2__caption' src={vn1} alt='' />
                </label>
            </div>   
            <button className='step1__right-arrow' />
        </div>
    )
}
