import React from "react";
import Popup from "./Popup";
// import hoodyBlack from '../images/hoody_black.png';
// import hoodyWhite from '../images/hoody_white.png';
// import hoodyGrey from '../images/hoody_grey.png';
import { selectedHoody } from "./Step1";
import polovina from '../images/polovina.png';
import full from '../images/full.png';
import vn from '../images/vn.png';
import fullPocket from '../images/fullPocket.png';
import squarePocket from '../images/squarePocket.png';
import circlePocket from '../images/circlePocket.png';
import vnPocket from '../images/vnPocket.png';

let selectedCaption = '';

export default function Step2() {

    function handleCaptionLeft(){

        const container = document.querySelector('.step1__choose');

        if (container) {
            const input3 = container.children[3];
            const input6 = container.children[6];
            const input0 = container.children[0];
            const label1 = container.children[1];
            const label4 = container.children[4];
            const label7 = container.children[7];

            if (input0 && input3 && input6 && label1 && label4 && label7) {
                input3.attributes.defaultChecked = false
                input0.classList.remove('center')
                input6.attributes.defaultChecked = true
                input3.classList.add('center')
                let saveInner1 = input0.innerHTML
                let saveInner2 = label1.innerHTML
                input0.innerHTML = input3.innerHTML
                label1.innerHTML = label4.innerHTML
                input3.innerHTML = input6.innerHTML
                label4.innerHTML = label7.innerHTML
                input6.innerHTML = saveInner1
                label7.innerHTML = saveInner2;
                selectedCaptions();
                handlePockets();
            } else {
                console.log('Not found')
            }
        }
    }

    function handleCaptionRight(){

        const container = document.querySelector('.step1__choose');

        if (container) {
            const input3 = container.children[3];
            const input6 = container.children[6];
            const input0 = container.children[0];
            const label1 = container.children[1];
            const label4 = container.children[4];
            const label7 = container.children[7];

            if (input0 && input3 && input6 && label1 && label4 && label7) {
                input3.attributes.defaultChecked = false
                input6.classList.remove('center')
                input0.attributes.defaultChecked = true
                input3.classList.add('center')
                let saveInner1 = input6.innerHTML
                let saveInner2 = label7.innerHTML
                input6.innerHTML = input3.innerHTML
                label7.innerHTML = label4.innerHTML
                input3.innerHTML = input0.innerHTML
                label4.innerHTML = label1.innerHTML
                input0.innerHTML = saveInner1
                label1.innerHTML = saveInner2;
                selectedCaptions();
                handlePockets();
            } else {
                console.log('Not found')
            }

        }     
    }

    function handlePocketLeft() {

        let centerCaption = document.querySelector('#centerCaption')

        if (centerCaption === fullPocket) {

        }
    }

    function hidePopup(){
        document.querySelector('#popup').classList.remove('popup_opened')
    }

    function selectedCaptions(){

        const radios = document.querySelectorAll('.step1__radio');
        let centerCaption = document.querySelector('#centerCaption')

        if (radios) {
            for (let i = 0; i < radios.length; i++) {
                // eslint-disable-next-line no-cond-assign
                if (radios[i].checked === true) {
                    selectedCaption = radios[i].labels[0].children[1].attributes.src.nodeValue;
                    console.log(selectedCaption)
                return
                }
            }
        }
    }

    function handlePockets() {

        let centerCaption = document.querySelector('.center').labels[0].children[1].alt
        const leftPocket = document.querySelector('#pocketLeft')
        const rigthPocket = document.querySelector('#pocketRight')

        if (centerCaption === 'full') {
            leftPocket.classList.add('step1__left-arrow_hidden');
            rigthPocket.classList.add('step1__right-arrow_hidden');
        } else {
            leftPocket.classList.remove('step1__left-arrow_hidden');
            rigthPocket.classList.remove('step1__right-arrow_hidden');
        }

        console.log(centerCaption)
    }

    // selectedCaptions();
    setTimeout(hidePopup, 3000)
    setTimeout(handlePockets, 2900)

    return(
        <div className='step1'>
            <Popup 
            number='02'
            text='Теперь дизайн капюшона и карманов'
            />
            <div className='step1__choose'>
                <input name='step2' type='radio' id='radio-11' value='' className='step1__radio' />
                <label className='step1__variant' for='radio-11'>
                    <img className='step1__image' src={selectedHoody} alt='' />
                    <img className='step2__caption' src={polovina} alt='polovina' />
                    <img className='step2__pocket' src={squarePocket} alt='' />
                </label>
                <div className='step2__arrows'>
                    <button onClick={handleCaptionLeft} className='step1__left-arrow' />
                    <button id='pocketLeft' className='step1__left-arrow' />
                </div>
                <input name='step2' type='radio' id='radio-12' value='' className='step1__radio center' defaultChecked />
                <label className='step1__variant' for='radio-12'>
                    <img className='step1__image' src={selectedHoody} alt='' />
                    <img className='step2__caption' src={full} alt='full' />
                    <img className='step2__pocket' src={fullPocket} alt='' />
                </label>
                <div className='step2__arrows'>
                    <button onClick={handleCaptionRight} className='step1__right-arrow' />
                    <button id='pocketRight' className='step1__right-arrow' />
                </div>
                <input name='step2' type='radio' id='radio-13' value='' className='step1__radio' />
                <label className='step1__variant' for='radio-13'>
                    <img className='step1__image' src={selectedHoody} alt='' />
                    <img className='step2__caption' src={vn} alt='vn' />
                    <img className='step2__pocket' src={circlePocket} alt='' />
                </label>
            </div>   
        </div>
    )
}
