import React from 'react';
import {Card, Container, Form} from "react-bootstrap";

const Auth = () => {
    return (
        <Container
            style={{height: window.innerHeight - 60, display: "flex", alignItems: "center", justifyContent: "center"}}
        >
            <Card style={{width: 600}} className='p-5'>
                <h2 style={{margin: "auto", textAlign: 'center'}}>Авторизация</h2>
                <Form style={{display: "flex", flexDirection: "column", alignItems: "center"}}>
                    <Form.Control
                        style={{marginTop: 20, height: 20, borderRadius: 5, width: 300}}
                        placeholder='Введите email...'
                    />
                    <Form.Control
                        style={{marginTop: 10, height: 20, borderRadius: 5, width: 300}}
                        placeholder='Введите пароль...'
                    />
                </Form>
            </Card>
        </Container>
    );
};

export default Auth;
