import React from "react";
import hoodyBlack from '../images/hoody_black.jpg';
import hoodyWhite from '../images/hoody_white.jpg';
import hoodyGrey from '../images/hoody_grey.jpg';


export default function Step1() {
    return(
        <div className='step1'>
            <button className='step1__left-arrow' />
            <div className='step1__choose'>
                <input type='radio' id='radio-1' className='step1__radio' />
                <label className='step1__variant' for='radio-1'>
                    <img className='step1__image' src={hoodyWhite} />
                </label>
                <input type='radio' id='radio-2' className='step1__radio' />
                <label className='step1__variant' for='radio-2'>
                    <img className='step1__image' src={hoodyBlack} />
                </label>
                <input type='radio' id='radio-3' className='step1__radio' />
                <label className='step1__variant' for='radio-3'>
                    <img className='step1__image' src={hoodyGrey} />
                </label>
            </div>   
            <button className='step1__right-arrow' />
        </div>
    )
}
