import React from "react";
import Popup from "./Popup";
import { selectedHoody } from "./Step1";
import baseBlack from '../images/base__a_black.png';
import baseWhite from '../images/base__a_white.png';
import baseGrey from '../images/base__a_grey.png';
import black0circle from '../images/base__d_black.png';
import black1banapr from '../images/b__black__banana_apricot.png';
import black2pocket from '../images/base__c_black.png';
import white0circle from '../images/base__d_white.png';
import white1banapr from '../images/b__white__banana_apricot.png';
import white2pocket from '../images/base__c_white.png';
import grey0circle from '../images/base__d_grey.png';
import grey1banapr from '../images/b__grey__banana_apricot.png';
import grey2pocket from '../images/base__c_grey.png';

let blackBasePack = [black0circle, black1banapr, black2pocket];
let whiteBasePack = [white0circle, white1banapr, white2pocket];
let greyBasePack = [grey0circle, grey1banapr, grey2pocket];

const baseUrl = 'http://u115964.test-handyhost'
let selectedCaption = '';
let selectedPocket = '';
let selectedBases = [];

export default function Step2() {

    function handleBase() {
        const images = document.querySelectorAll('.step1__image')

        if (selectedHoody === baseBlack) {
            for (let i = 0; i < blackBasePack.length; i++) {
                selectedBases[i] = blackBasePack[i]
                images[i].src = baseUrl + selectedBases[i]
            }
        } else if (selectedHoody === baseWhite) {
            for (let i = 0; i < whiteBasePack.length; i++) {
                selectedBases[i] = whiteBasePack[i]
                images[i].src = baseUrl + selectedBases[i]
            }
        } else if (selectedHoody === baseGrey) {
            for (let i = 0; i < greyBasePack.length; i++) {
                selectedBases[i] = greyBasePack[i]
                images[i].src = baseUrl + selectedBases[i]
            }
        } else {
            console.log('Not found')
        }
    }

    function hidePopup(){
        document.querySelector('#popup').classList.remove('popup_opened')
    }

    handleBase()
    setTimeout(hidePopup, 3000)

    return(
        <div className='step1'>
            <Popup 
            number='02'
            text='Теперь дизайн капюшона и карманов'
            />
            <div className='step1__choose'>
                <input name='step2' type='radio' id='radio-11' value='' className='step1__radio' disabled='true' />
                <label className='step1__variant' for='radio-11'>
                    <img className='step1__image' src={selectedBases[0]} alt='' />
                </label>
                <div className='step2__arrows'>
                    <button className='step1__left-arrow' />
                    <button id='pocketLeft' className='step1__left-arrow' />
                </div>
                <input name='step2' type='radio' id='radio-12' value='' className='step1__radio center' defaultChecked />
                <label className='step1__variant' for='radio-12'>
                    <img className='step1__image' src={selectedBases[1]} alt='' />
                </label>
                <div className='step2__arrows'>
                    <button className='step1__right-arrow' />
                    <button id='pocketRight' className='step1__right-arrow' />
                </div>
                <input name='step2' type='radio' id='radio-13' value='' className='step1__radio' disabled='true' />
                <label className='step1__variant' for='radio-13'>
                    <img className='step1__image' src={selectedBases[2]} alt='' />
                </label>
            </div>   
        </div>
    )
}

export {selectedPocket, selectedCaption}
