export default function Color(variant) {

    function choose(e) {
        const colors = document.querySelectorAll('.color')

        for (let i = 0; i < colors.length; i++) {
            if (e.target === colors[i]) {
                colors[i].classList.add('color_big')
                return
            } else {
                console.log('Хуета')
            }
        }
    }

    return (
        <li onClick={choose} className={`color ${variant}`} />
    )
}
