import React from 'react';
import {Button, Card, Container, Form} from "react-bootstrap";
import {NavLink, useLocation} from "react-router-dom";
import {LOGIN_ROUTE, REGISTRATION_ROUTE} from "../utils/consts";

const Auth = () => {
    const location = useLocation()
    const isLogin = location.pathname === LOGIN_ROUTE
    console.log(location)

    return (
        <Container
            style={{height: window.innerHeight - 60, display: "flex", alignItems: "center", justifyContent: "center"}}
        >
            <Card style={{width: 600}} className='p-5'>
                <h2 style={{margin: "auto", textAlign: 'center'}}>{isLogin ? 'Авторизация' : 'Регистрация'}</h2>
                <Form style={{display: "flex", flexDirection: "column", alignItems: "center"}}>
                    <Form.Control
                        style={{marginTop: 20, height: 20, borderRadius: 5, width: 300}}
                        placeholder='Введите email...'
                    />
                    <Form.Control
                        style={{marginTop: 10, height: 20, borderRadius: 5, width: 300}}
                        placeholder='Введите пароль...'
                    />
                    <div style={{display: 'flex', marginTop: 20, flexDirection: 'row-reverse', justifyContent: 'space-between', gap: 100}}>
                        <Button style={{height: 30, borderRadius: 5, width: 100, cursor: 'pointer'}}>
                            Войти
                        </Button>
                        {isLogin ?
                            <NavLink style={{textDecoration: 'none', color: 'black'}}
                                     to={REGISTRATION_ROUTE}>Регистрация</NavLink>
                            :
                            <NavLink style={{textDecoration: 'none', color: 'black'}}
                                     to={REGISTRATION_ROUTE}>Авторизация</NavLink>
                        }
                    </div>
                </Form>
            </Card>
        </Container>
    );
};

export default Auth;
